func bigIntFromString(s string) *big.Int {
	n, _ := big.NewInt(0).SetString(s, 10)
	return n
}

func bigIntFromStringHex(s string) *big.Int {
	n, _ := big.NewInt(0).SetString(s, 16)
	return n
}

